// In src/components/PersonalDashboardPage.js
import React, { useState } from 'react';
import HelpPage from './HelpPage';
import './css/dashboard.css'; // Make sure to create a corresponding CSS file for styling

function PersonalDashboardPage({ user }) {
    const [helpModalOpen, setHelpModalOpen] = useState(false);
    const [formData, setFormData] = useState({
        name: user.name,
        // Add other fields as needed
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // You can implement the logic to update user information here
        console.log(formData); // Example: send formData to server or update state
    };

    const toggleHelpModal = () => {
        setHelpModalOpen(!helpModalOpen);
    };

    return (
        <div className="profile-header">
            <span className="profile-name">Personal Dashboard</span>
            <p>Hello, {user.name}</p>

            <button className="help-button" onClick={toggleHelpModal}>?</button>
            {helpModalOpen && <HelpPage onClose={toggleHelpModal} />}

            <form onSubmit={handleSubmit} className="dashboard-form">
                <label>
                    Name:
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                    />
                </label>
                {/* Add more fields as needed */}
                <button type="submit" className="save-button">Save</button>
            </form>
        </div>
    );
}

export default PersonalDashboardPage;
