// AdvicePage.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PostReply from './PostReply'; // Make sure this path matches your file structure
import './css/advicePage.css'; // Assuming you have a separate CSS file for AdvicePage styles
import CreateAdvicePost from './CreateAdvicePost';

function AdvicePage() {
  const [posts, setPosts] = useState([]);

  const fetchPosts = async () => {
    try {
      const response = await axios.get('/api/advice');
      setPosts(response.data);
    } catch (error) {
      console.error('There was an error fetching the advice posts:', error);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  return (
    <div className="advice-page">
      <h1>Welcome to New Parents' Advice Corner</h1>
      <div className="advice-posts">
        {posts.map((post) => (
          <div key={post.id} className="advice-post">
            <p>{post.question}</p>
            {post.replies && post.replies.map(reply => (
              <div key={reply.id} className="reply">
                <p>{reply.text}</p>
              </div>
            ))}
            <PostReply postId={post.id} refreshPosts={fetchPosts} />
          </div>
        ))}
        <CreateAdvicePost refreshPage={fetchPosts}></CreateAdvicePost>
      </div>
    </div>
  );
}

export default AdvicePage;
