import React, { useState } from 'react';
import './css/shoppingPage.css';

function ShoppingPage({ onBackClick }) {
  const [shoppingLists, setShoppingLists] = useState([]);
  const [currentList, setCurrentList] = useState(null);
  const [newListName, setNewListName] = useState('');
  const [newCategory, setNewCategory] = useState('');
  const [newItem, setNewItem] = useState({ name: '', count: 1, cost: 0, purchased: false });

  const defaultCategories = ['Produce', 'Dairy', 'Meat', 'Utilities'];

  const handleCreateList = () => {
    if (newListName.trim() !== '') {
      const newList = {
        id: Date.now(),
        name: newListName,
        categories: defaultCategories.map((category) => ({ name: category, items: [] })),
      };
      setShoppingLists([...shoppingLists, newList]);
      setNewListName('');
    }
  };

  const handleDeleteList = (listId) => {
    setShoppingLists(shoppingLists.filter((list) => list.id !== listId));
    setCurrentList(null);
  };

  const handleAddCategory = () => {
    if (newCategory.trim() !== '') {
      const updatedList = {
        ...currentList,
        categories: [...currentList.categories, { name: newCategory, items: [] }],
      };
      setShoppingLists(shoppingLists.map((list) => (list.id === currentList.id ? updatedList : list)));
      setNewCategory('');
    }
  };

  const handleUpdateItem = (categoryName, itemName, field, value) => {
    const updatedList = {
      ...currentList,
      categories: currentList.categories.map((category) =>
        category.name === categoryName
          ? {
              ...category,
              items: category.items.map((item) =>
                item.name === itemName ? { ...item, [field]: value } : item
              ),
            }
          : category
      ),
    };
    setShoppingLists(shoppingLists.map((list) => (list.id === currentList.id ? updatedList : list)));
  };

  const handleDeleteCategory = (categoryName) => {
    const updatedList = {
      ...currentList,
      categories: currentList.categories.filter((category) => category.name !== categoryName),
    };
    setShoppingLists(shoppingLists.map((list) => (list.id === currentList.id ? updatedList : list)));
  };

  const handleAddItem = (categoryName) => {
    if (newItem.name.trim() !== '') {
      const updatedList = {
        ...currentList,
        categories: currentList.categories.map((category) =>
          category.name === categoryName ? { ...category, items: [...category.items, newItem] } : category
        ),
      };
      setShoppingLists(shoppingLists.map((list) => (list.id === currentList.id ? updatedList : list)));
      setNewItem({ name: '', count: 1, cost: 0, purchased: false });
    }
  };

  const handleDeleteItem = (categoryName, itemName) => {
    const updatedList = {
      ...currentList,
      categories: currentList.categories.map((category) =>
        category.name === categoryName ? { ...category, items: category.items.filter((item) => item.name !== itemName) } : category
      ),
    };
    setShoppingLists(shoppingLists.map((list) => (list.id === currentList.id ? updatedList : list)));
  };

  const handleTogglePurchased = (categoryName, itemName) => {
    const updatedList = {
      ...currentList,
      categories: currentList.categories.map((category) =>
        category.name === categoryName
          ? {
              ...category,
              items: category.items.map((item) => (item.name === itemName ? { ...item, purchased: !item.purchased } : item)),
            }
          : category
      ),
    };
    setShoppingLists(shoppingLists.map((list) => (list.id === currentList.id ? updatedList : list)));
  };

  const calculateTotalCost = () => {
    let totalCost = 0;
    currentList.categories.forEach((category) => {
      category.items.forEach((item) => {
        totalCost += item.count * item.cost;
      });
    });
    return totalCost.toFixed(2);
  };

  return (
    <div className="shopping-page-container">
      <h2>Shopping Lists</h2>
      <div className="shopping-lists">
        {shoppingLists.map((list) => (
          <div key={list.id} className="shopping-list" onClick={() => setCurrentList(list)}>
            {list.name}
            <button className="delete-list-button" onClick={() => handleDeleteList(list.id)}>
              Delete
            </button>
          </div>
        ))}
      </div>
      <div className="create-list">
        <input
          type="text"
          placeholder="New List Name"
          value={newListName}
          onChange={(e) => setNewListName(e.target.value)}
        />
        <button className="create-list-button" onClick={handleCreateList}>
          Create List
        </button>
      </div>
      {currentList && (
        <div className="current-list">
          <h3>{currentList.name}</h3>
          <div className="categories">
            {currentList.categories.map((category) => (
              <div key={category.name} className="category">
                <h4>{category.name}</h4>
                <div className="items">
                  {category.items.map((item) => (
                    <div key={item.name} className="item">
                      <input
                        type="text"
                        value={item.name}
                        readOnly
                      />
                      <input
                        type="number"
                        value={item.count}
                        onChange={(e) =>
                          handleUpdateItem(category.name, item.name, 'count', parseInt(e.target.value))
                        }
                      />
                      <input
                        type="number"
                        value={item.cost}
                        onChange={(e) =>
                          handleUpdateItem(category.name, item.name, 'cost', parseFloat(e.target.value))
                        }
                      />
                      <button
                        className={`purchased-button ${item.purchased ? 'purchased' : ''}`}
                        onClick={() => handleTogglePurchased(category.name, item.name)}
                      ></button>
                      <button
                        className="delete-item-button"
                        onClick={() => handleDeleteItem(category.name, item.name)}
                      >
                        Delete
                      </button>
                    </div>
                  ))}
                </div>
                <div className="add-item">
                  <input
                    type="text"
                    placeholder="New Item Name"
                    value={newItem.name}
                    onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
                  />
                  <input
                    type="number"
                    placeholder="Count"
                    value={newItem.count}
                    onChange={(e) => setNewItem({ ...newItem, count: parseInt(e.target.value) })}
                  />
                  <input
                    type="number"
                    placeholder="Cost"
                    value={newItem.cost}
                    onChange={(e) => setNewItem({ ...newItem, cost: parseFloat(e.target.value) })}
                  />
                  <button className="add-item-button" onClick={() => handleAddItem(category.name)}>
                    Add Item
                  </button>
                </div>
                <button className="delete-category-button" onClick={() => handleDeleteCategory(category.name)}>
                  Delete Category
                </button>
              </div>
            ))}
          </div>
          <div className="add-category">
            <input
              type="text"
              placeholder="New Category Name"
              value={newCategory}
              onChange={(e) => setNewCategory(e.target.value)}
            />
            <button className="add-category-button" onClick={handleAddCategory}>
              Add Category
            </button>
          </div>
          <div className="total-cost">Total Cost: ${calculateTotalCost()}</div>
        </div>
      )}
      <button className="back-button" onClick={onBackClick}>
        Back
      </button>
    </div>
  );
}

export default ShoppingPage;
