import React, { useState, useEffect } from 'react';
import './css/communityPage.css';

function CommunityPage() {
  const [communities, setCommunities] = useState([]);
  const [selectedCommunity, setSelectedCommunity] = useState(null);
  const [communityPosts, setCommunityPosts] = useState([]);
  const [newPostTitle, setNewPostTitle] = useState('');
  const [newPostContent, setNewPostContent] = useState('');
  const [newCommunityName, setNewCommunityName] = useState('');
  const [newCommunityDescription, setNewCommunityDescription] = useState('');

  useEffect(() => {
    fetchCommunities();
  }, []);

  const fetchCommunities = async () => {
    try {
      const response = await fetch('/api/communities');
      const data = await response.json();
      setCommunities(data);
    } catch (error) {
      console.error('Error fetching communities:', error);
    }
  };

  const fetchCommunityPosts = async (communityId) => {
    try {
      const response = await fetch(`/api/communities/${communityId}/posts`);
      const data = await response.json();
      setCommunityPosts(data);
    } catch (error) {
      console.error('Error fetching community posts:', error);
    }
  };

  const handleCommunityClick = (community) => {
    setSelectedCommunity(community);
    fetchCommunityPosts(community.id);
  };

  const handleJoinCommunity = async (communityId) => {
    try {
      await fetch(`/api/communities/${communityId}/join`, { method: 'POST' });
      fetchCommunities();
    } catch (error) {
      console.error('Error joining community:', error);
    }
  };

  const handleLeaveCommunity = async (communityId) => {
    try {
      await fetch(`/api/communities/${communityId}/leave`, { method: 'POST' });
      fetchCommunities();
      setSelectedCommunity(null);
      setCommunityPosts([]);
    } catch (error) {
      console.error('Error leaving community:', error);
    }
  };

  const handleCreateCommunity = async () => {
    try {
      const response = await fetch('/api/communities', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: newCommunityName, description: newCommunityDescription }),
      });
      const data = await response.json();
      setNewCommunityName('');
      setNewCommunityDescription('');
      fetchCommunities();
    } catch (error) {
      console.error('Error creating community:', error);
    }
  };

  const handleCreatePost = async () => {
    try {
      const response = await fetch(`/api/communities/${selectedCommunity.id}/posts`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title: newPostTitle, content: newPostContent }),
      });
      const data = await response.json();
      setNewPostTitle('');
      setNewPostContent('');
      fetchCommunityPosts(selectedCommunity.id);
    } catch (error) {
      console.error('Error creating post:', error);
    }
  };

  const handleReplyToPost = async (postId, replyContent) => {
    try {
      await fetch(`/api/communities/${selectedCommunity.id}/posts/${postId}/replies`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ content: replyContent }),
      });
      fetchCommunityPosts(selectedCommunity.id);
    } catch (error) {
      console.error('Error replying to post:', error);
    }
  };

  return (
    <div className="community-page">
      <div className="community-sidebar">
        <h2>Communities</h2>
        <ul>
          {communities.map((community) => (
            <li
              key={community.id}
              onClick={() => handleCommunityClick(community)}
              className={selectedCommunity && selectedCommunity.id === community.id ? 'active' : ''}
            >
              {community.name}
              {selectedCommunity && selectedCommunity.id === community.id ? (
                <button onClick={() => handleLeaveCommunity(community.id)}>Leave</button>
              ) : (
                <button onClick={() => handleJoinCommunity(community.id)}>Join</button>
              )}
            </li>
          ))}
        </ul>
        <div className="create-community">
          <h3>Create Community</h3>
          <input
            type="text"
            placeholder="Community Name"
            value={newCommunityName}
            onChange={(e) => setNewCommunityName(e.target.value)}
          />
          <textarea
            placeholder="Community Description"
            value={newCommunityDescription}
            onChange={(e) => setNewCommunityDescription(e.target.value)}
          ></textarea>
          <button onClick={handleCreateCommunity}>Create</button>
        </div>
      </div>
      <div className="community-content">
        {selectedCommunity ? (
          <>
            <h2>{selectedCommunity.name}</h2>
            <div className="create-post">
              <h3>Create Post</h3>
              <input
                type="text"
                placeholder="Title"
                value={newPostTitle}
                onChange={(e) => setNewPostTitle(e.target.value)}
              />
              <textarea
                placeholder="Content"
                value={newPostContent}
                onChange={(e) => setNewPostContent(e.target.value)}
              ></textarea>
              <button onClick={handleCreatePost}>Create</button>
            </div>
            <div className="community-posts">
              {communityPosts.map((post) => (
                <div key={post.id} className="post">
                  <h4>{post.title}</h4>
                  <p>{post.content}</p>
                  <div className="post-replies">
                    {post.replies.map((reply) => (
                      <div key={reply.id} className="reply">
                        <p>{reply.content}</p>
                      </div>
                    ))}
                  </div>
                  <div className="reply-input">
                    <input
                      type="text"
                      placeholder="Reply..."
                      onKeyPress={(e) => {
                        if (e.key === 'Enter') {
                          handleReplyToPost(post.id, e.target.value);
                          e.target.value = '';
                        }
                      }}
                    />
                  </div>
                </div>
              ))}
            </div>
          </>
        ) : (
          <p>Select a community to view posts.</p>
        )}
      </div>
    </div>
  );
}

export default CommunityPage;
