import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './css/advicePosts.css';
import CreateAdvicePost from './CreateAdvicePost';
import './css/advicePosts.css';
import PostReply from './PostReply';

function AdvicePosts({ user }) {
  const [posts, setPosts] = useState([]);
  const [profilePictureUrls, setProfilePictureUrls] = useState({});

  const fetchPosts = () => {
    axios
      .get('/api/advice')
      .then(response => setPosts(response.data))
      .catch(error => console.error('There was an error fetching the advice posts:', error));
  };

  useEffect(() => {
    fetchPosts();
    posts.forEach(post => {
      updateProfilePictureUrl(post.user_id);
      post.replies.forEach(reply => {
        updateProfilePictureUrl(reply.user_id);
      });
    });
  }, [posts]);

  const handleDeletePost = postId => {
    axios
      .delete(`/api/advice/${postId}`)
      .then(() => {
        alert('Deleted post!');
        fetchPosts(); // Refresh the posts after deleting
      })
      .catch(error => console.error('There was an error deleting the advice post:', error));
  };

  const handleDeleteReply = (postId, replyId) => {
    axios
      .delete(`/api/advice/${postId}/reply/${replyId}`)
      .then(() => {
        alert('Deleted reply!');
        fetchPosts(); // Refresh the posts after deleting
      })
      .catch(error => console.error('There was an error deleting the reply:', error));
  };

  const fetchProfilePicture = async userId => {
    try {
      const response = await axios.get(`/profile_picture/${userId}`, {
        withCredentials: true,
        responseType: 'blob'
      });
      const profilePictureBlob = new Blob([response.data], { type: 'image/jpeg' });
      return URL.createObjectURL(profilePictureBlob);
    } catch (error) {
      console.error('Error fetching profile picture:', error);
      return null;
    }
  };

  const updateProfilePictureUrl = async userId => {
    try {
      const profilePictureUrl = await fetchProfilePicture(userId);
      setProfilePictureUrls(prevUrls => ({
        ...prevUrls,
        [userId]: profilePictureUrl
      }));
    } catch (error) {
      console.error('Error updating profile picture URL:', error);
    }
  };

  return (
    <div className="advice-posts">
      {posts.map(post => (
        <div key={post.id} className="advice-post">
          <div className="post-content">
            <div className="post-author">
              {profilePictureUrls[post.user_id] && (
                <img src={profilePictureUrls[post.user_id]} alt="Author Profile" className="profile-picture" />
              )}
              <strong>Question by {post.author}: </strong>
            </div>
            <p>{post.question}</p>
            {user.name === post.author && (
              <button className="delete-btn" onClick={() => handleDeletePost(post.id)}>
                Delete
              </button>
            )}
          </div>
          <div className="replies-container">
            {post.replies && post.replies.map(reply => (
              <div key={reply.id} className="reply">
                <div className="reply-author">
                  {profilePictureUrls[reply.user_id] && (
                    <img src={profilePictureUrls[reply.user_id]} alt="Author Profile" className="profile-picture" />
                  )}
                  <strong>Reply by {reply.author}: </strong>
                </div>
                <p>{reply.text}</p>
                {user.name === reply.author && (
                  <button className="delete-btn" onClick={() => handleDeleteReply(post.id, reply.id)}>
                    Delete
                  </button>
                )}
              </div>
            ))}
            <PostReply postId={post.id} refreshPage={fetchPosts} />
          </div>
        </div>
      ))}
      <CreateAdvicePost refreshPage={fetchPosts} />
    </div>
  );
}

export default AdvicePosts;
