import React from 'react';
import './css/resourcesPage.css'; // Make sure your CSS file is named correctly
import BudgetingPage from './resources/BudgetingPage.jsx';

function ResourcesPage() {
  const [activeResource, setActiveResource] = useState(null);

  const resources = [
    // ... your resources array with an additional component attribute, if needed
    { name: 'Budgeting', icon: 'budget-icon', color: 'red', component: BudgetingPage },
    // ... other resources
  ];

  const handleResourceClick = (resourceComponent) => {
    setActiveResource(() => resourceComponent);
  };

  const handleBackClick = () => {
    setActiveResource(null);  // Reset to show the main grid
  };

  return (
    <div className="resources-container">
      <h2>Resources</h2>
      {activeResource ? (
        // If a resource component has been selected, render it
        <activeResource onBackClick={handleBackClick} />
      ) : (
        // Otherwise, show the default grid of resources
        <div className="resources-grid">
          {resources.map((resource, index) => (
            <div key={index} className="resource-card" style={{ backgroundColor: resource.color }} onClick={() => handleResourceClick(resource.component)}>
              <div className="resource-icon">{resource.icon}</div>
              <div className="resource-name">{resource.name}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ResourcesPage;
