import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './css/parksPage.css';
import { GOOGLE_MAPS_API_KEY } from './mapsAPI';




function ParksPage({ onBackClick }) {
  const [location, setLocation] = useState(null);
  const [radius, setRadius] = useState(1000);
  const [parks, setParks] = useState([]);
  const [sortBy, setSortBy] = useState('rating');

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
        },
        (error) => {
          console.error('Error getting location:', error);
        }
      );
    } else {
      console.error('Geolocation is not supported by this browser.');
    }
  }, []);

  useEffect(() => {
    if (location) {
      fetchParks();
    }
  }, [location, radius, sortBy]);

  const fetchParks = async () => {
    try {
      const response = await axios.get(
        `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${location.lat},${location.lng}&radius=${radius}&type=park&key=${GOOGLE_MAPS_API_KEY}`,
        {'headers': 'Access-Control-Allow-Origin',}
      );
      const parksData = response.data.results;

      const parksWithDetails = await Promise.all(
        parksData.map(async (park) => {
          const detailsResponse = await axios.get(
            `https://maps.googleapis.com/maps/api/place/details/json?place_id=${park.place_id}&fields=name,rating,review_count,photos&key=${GOOGLE_MAPS_API_KEY}`
          );
          const details = detailsResponse.data.result;
          return {
            ...park,
            rating: details.rating,
            reviewCount: details.review_count,
            photoUrl: details.photos ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${details.photos[0].photo_reference}&key=${GOOGLE_MAPS_API_KEY}` : null,
          };
        })
      );

      setParks(parksWithDetails);
    } catch (error) {
      console.error('Error fetching parks:', error);
    }
  };

  const sortParks = (parks) => {
    switch (sortBy) {
      case 'rating':
        return parks.sort((a, b) => b.rating - a.rating);
      case 'reviewCount':
        return parks.sort((a, b) => b.reviewCount - a.reviewCount);
      case 'distance':
        return parks.sort((a, b) => a.distance - b.distance);
      default:
        return parks;
    }
  };

  return (
    <div className="parks-page-container">
      <h2>Parks Guide</h2>
      <div className="radius-slider">
        <label>Radius: {radius} meters</label>
        <input
          type="range"
          min="100"
          max="10000"
          step="100"
          value={radius}
          onChange={(e) => setRadius(Number(e.target.value))}
        />
      </div>
      <div className="sort-dropdown">
        <label>Sort by:</label>
        <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="rating">Rating</option>
          <option value="reviewCount">Review Count</option>
          <option value="distance">Distance</option>
        </select>
      </div>
      <div className="parks-list">
        {sortParks(parks).map((park) => (
          <div key={park.place_id} className="park-item">
            {park.photoUrl && <img src={park.photoUrl} alt={park.name} />}
            <div className="park-details">
              <h3>{park.name}</h3>
              <p>Rating: {park.rating}</p>
              <p>Reviews: {park.reviewCount}</p>
            </div>
          </div>
        ))}
      </div>
      <button className="back-button" onClick={onBackClick}></button>
    </div>
  );
}

export default ParksPage;
